import React, { useEffect, useState } from 'react'
import Container from 'react-bootstrap/Container';
import styled from 'styled-components';
import Typography from '@mui/material/Typography';
import leftbg from "../assets/images/left-bg.png";
import Teammember from '../components/Teammember';



const Team = () => {
  const Teamwrapper = styled.div`
    position: relative;
    background-color: white;
    background-image: url(${leftbg});
    background-size: auto;
    background-repeat: no-repeat;
    background-position: top left;
  h3{
     color:${({theme})=>theme.colors.headeractive};
     font-family:inherit;
     margin-bottom:20px;
     margin-top:0px!important;
   }
   span{
        color:${({theme})=>theme.colors.text7};
        position: relative;
        font-size:14px;
  }

  .team_member{
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
    gap:20px;

    .card{
      flex:0 1 300px;
      padding:16px;
      background:transparent;
      align-items: center;
      .card_image{
        border-radius:50%;
        overflow:hidden;
        border: 1px solid #5a1466;
        width:60%;
        position:relative;
        width:200px;
        height:200px;
        img{
          position:absolute;
        }
        .imagefront{
          opacity:1;
          transition: all 0.5s linear;

        }
        .imageback{
          opacity:0;
          background:black;
          transition: all 0.3s linear;
          transform: translate(-200px, 0px);
        }
      }
      .card_details{
        h4{
          font-size: 16px;
          margin-top:12px;
        }
        h5{
          color: #5a1466;
          font-size: 16px;
        }
        h6{
          font-size: 18px;
          font-weight: 300;
          letter-spacing: 2px;
        }
      }
    }

    .card:hover .imageback{
      opacity:1;
      transform: translate(0px, 0px);
    }

    .card:hover .card_details{
      text-shadow: 1px 1px 0 #ccc,
    2px 2px 0 #c9c9c9,
    3px 3px 0 #bbb,
    4px 4px 0 #b9b9b9,
    5px 5px 0 #aaa,
    6px 6px 0 #a8a8a8,
    7px 7px 0 #999,
    8px 8px 0 #979797,
    9px 9px 0 #888,
    10px 10px 0 #868686;
    }
    
  }
  `


  const [team, setTeam]= useState([]);

  useEffect(()=>{
    const fetchData= async()=>{
      try{
        const response = await fetch('./data/teammember.json');
        const data = await response.json();
        setTeam(data.teams)
      }catch(err){
        console.error(err);
      }
    }

    fetchData();
  },[])
  
  console.log('team page');
  console.log(team);
  
  return (
    <Teamwrapper>
      <Container className='announcementwrapper common_margin'>
        <Typography variant="h3" className='mt-3'>Teams</Typography>
        <span>below are the Team members list</span>
        <section className='team_member'>
          {
            team.map((val,idx)=>{
              return (
                <Teammember
                  key={idx}
                  {...val}

                />
              )
            })
          }
        
        </section>
      </Container>
    </Teamwrapper>
  )
}

export default Team
